mapper.select:

	SELECT empno, name, password
	 FROM mega_user
	WHERE empno = #{empno}

mapper.insert:
 	
 		INSERT INTO mega_user(
				empno
        		, name
        		, password
 		 ) VALUES (
 		   #{empno}
 		  ,#{name}
 		  ,#{password}
 		 )

mapper.update:
	
		UPDATE mega_user
		     SET password = #{password}
        		, name = #{name}
        WHERE empno = #{empno}
